mapper.select:
SELECT r.id AS reviewId /* 리뷰 ID*/ , r.title AS title /* 리뷰 제목 */ , r.content AS content /* 리뷰 내용(html) */ , r.status AS status /* 리뷰 상태(읽음, 완독...)*/ , r.category AS category /* 리뷰 분류(소설, 과학, 문학...)*/ , r.tag AS tag /* 리뷰 태그(난이도, 분량...)*/ , r.created_at AS createdAt /* 리뷰 작성일 */ , m.id AS authorId /* 리뷰 작성자 ID */ , m.nickname AS nickname /* 리뷰 작성자 닉네임 */ FROM review r /* 리뷰 */ INNER JOIN member m /* 멤버 */ ON r.author_id = m.id WHERE 1=1
AND r.status = #{status}
AND r.category = #{category}
AND r.tag = #{tag}
AND (r.title LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY r.created_at DESC
ORDER BY r.created_at DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(1)
  FROM review r /* 리뷰 */
 INNER JOIN member m /* 멤버 */
    ON r.author_id = m.id
 WHERE 1=1

   AND r.status = #{status}


   AND r.category = #{category}


   AND r.tag = #{tag}


   AND (r.title LIKE CONCAT('%', #{keyword}, '%')
